  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=114 DEFAULT CHARSET=utf8;

/*Data for the table `t_cms` */

insert  into `t_cms`(`id`,`title`,`content`,`creater_id`,`creat_time`,`type_id`,`img_path`) values 
(111,'周东申版画展在山东工艺美院开幕','<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;10月17日上午，由山东工艺美术学院主办，现代手工艺学院承办的周东申教授版画展在我校长清校区图书馆一楼展厅举行。山东工艺美术学院院长潘鲁生、副院长张云龙、苗登宇,现代手工艺学院院长赵进、党总支书记刘慎华、副院长宋处岭，山东工艺美术学院各部门领导、校友和新闻媒体出席了此次展览。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周东申教授，1963年毕业于浙江美术学院（现中国美术学院）版画系。现为山东工艺美术学院教授、中国美术家协会会员、中国版画家协会会员、中国美协藏书票艺委会会员、山东藏书票学会名誉会长。在展览开幕式上，潘鲁生着重强调周东申先生的版画从题材到构思，推陈出新，以返璞归真的艺术风格，表现出艺术的真实性，不虚伪，不做作。周先生的作品视野开阔，富有气势，在他的笔下流露着一股自然的美感，给人以丰富的启迪和审美的愉悦。周东申教授把版画创作当成自己的事业，将东西方版画艺术的精髓融入自己的创作中，形成周氏版画的独特面貌。随后，各位来宾、领导及师生认真观看了展览。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;展览的近百幅作品，不仅是周东申教授的创作成果，也是他五十年执着的艺术结晶。本次展览将持续到10月22日结束。</p><p>&nbsp;</p><figure class=\"image\"><img src=\"/public/upload/myImage-1531713443978.jpg\"></figure>',1,'2018-07-16 03:58:00',2,'/public/upload/myImage-1531713415185.jpg'),
(112,'温琴佐-桑弗先生参观周东申版画展','<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;温琴佐·桑弗，济南市荣誉市民，意大利文化艺术中心主席，国际著名策展人，长期致力于国际文化交流。桑弗是济南人的老朋友，十几年来“来过济南不下100次”，由他策划并组织的“毕加索版画原作展”、“意大利当代设计艺术展”、“意大利雕塑展”等一系列展览在济南都产生了很大影响。</p><figure class=\"image\"><img src=\"/public/upload/myImage-1531713627848.png\"></figure>',1,'2018-07-16 04:00:29',4,'/public/upload/myImage-1531713593399.png'),
(113,'建校40周年学术活动之“周东申教授版画展”','<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;10月17日上午，由山东工艺美术学院主办，现代手工艺学院承办的周东申教授版画展在我校长清校区图书馆一楼展厅举行。山东工艺美术学院院长潘鲁生、副院长张云龙、苗登宇,现代手工艺学院院长赵进、党总支书记刘慎华、副院长宋处岭，山东工艺美术学院各部门领导、校友和新闻媒体出席了此次展览。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 周东申教授，1963年毕业于浙江美术学院（现中国美术学院）版画系。现为山东工艺美术学院教授、中国美术家协会会员、中国版画家协会会员、中国美协藏书票艺委会会员、山东藏书票学会名誉会长。在展览开幕式上，潘鲁生着重强调周东申先生的版画从题材到构思，推陈出新，以返璞归真的艺术风格，表现出艺术的真实性，不虚伪，不做作。周先生的作品视野开阔，富有气势，在他的笔下流露着一股自然的美感，给人以丰富的启迪和审美的愉悦。周东申教授把版画创作当成自己的事业，将东西方版画艺术的精髓融入自己的创作中，形成周氏版画的独特面貌。随后，各位来宾、领导及师生认真观看了展览。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 展览的近百幅作品，不仅是周东申教授的创作成果，也是他五十年执着的艺术结晶。本次展览将持续到10月22日结束。</p>',1,'2018-07-16 04:27:28',2,'/public/upload/myImage-1531715247367.jpg');

