/*Table structure for table `weixin_message` */

DROP TABLE IF EXISTS `weixin_message`;

CREATE TABLE `weixin_message` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '留言id',
  `target_type` varchar(10) DEFAULT NULL COMMENT '留言所属类型',
  `target_id` bigint(50) DEFAULT NULL COMMENT '所属类型中记录的id',
  `pid` bigint(20) DEFAULT '0' COMMENT '留言的父级id',
  `user_id` bigint(50) DEFAULT NULL COMMENT '留言者id',
  `content` varchar(1000) DEFAULT NULL COMMENT '留言内容',
  `create_time` varchar(20) DEFAULT NULL COMMENT '留言的创建时间',
  `top` int(2) DEFAULT '0' COMMENT '置顶 0：不置顶 1：置顶',
  `top_time` datetime DEFAULT NULL COMMENT '置顶时间',
  `liked` bigint(20) DEFAULT '0' COMMENT '点赞数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `weixin_message` */

/*Table structure for table `weixin_reply` */

DROP TABLE IF EXISTS `weixin_reply`;

CREATE TABLE `weixin_reply` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT COMMENT '回复表id',
  `message_id` bigint(20) DEFAULT NULL COMMENT '留言表id',
  `user_name` varchar(20) DEFAULT NULL COMMENT '回复人名称',
  `reply_time` varchar(20) DEFAULT NULL COMMENT '回复时间',
  `reply_content` varchar(1000) DEFAULT NULL COMMENT '回复内容',
  PRIMARY KEY (`id`),
  KEY `FK_weixin_reply` (`message_id`),
  CONSTRAINT `FK_weixin_reply` FOREIGN KEY (`message_id`) REFERENCES `weixin_message` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `weixin_reply` */

/*Table structure for table `weixin_user` */

DROP TABLE IF EXISTS `weixin_user`;

CREATE TABLE `weixin_user` (
  `id` int(6) NOT NULL AUTO_INCREMENT COMMENT '微信用户ID',
  `nickname` varchar(40) DEFAULT NULL COMMENT '微信昵称',
  `phone` varchar(13) DEFAULT NULL COMMENT '微信用户电话号码',
  `follow_time` varchar(40) DEFAULT NULL COMMENT '关注日期',
  `state` int(1) DEFAULT NULL COMMENT '微信公众号关注状态 0-未关注 1-已关注',
  `weixin` varchar(40) DEFAULT NULL COMMENT '微信号',
  `img` varchar(200) DEFAULT NULL COMMENT '头像',
  `identity` int(10) DEFAULT '0' COMMENT '身份 0：普通人 1：管理员',
  `openid` varchar(40) DEFAULT NULL,
  `is_able` int(2) DEFAULT '0' COMMENT '是否可用 0-可用 1-禁用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

